class TaskQueue {
  constructor(concurrency) {
    this.concurrency = concurrency;
    this.running = 0;
    this.queue = [];
  }
  pushTask(task) {
    this.queue.push(task);
    this.next();
  }

  next() {
    console.log(`Tasks :- Running [${this.running}] Queue [${this.queue.length}] Concurrency [${this.concurrency}]`);
    while(this.running < this.concurrency && this.queue.length) {
      const task = this.queue.shift();
      task().then(() => {
        this.running--;
        this.next();
      });
      this.running++;
    }
  }
};

module.exports = TaskQueue;